// Typescript configuration file to recognize the file type on import
declare module '*.jpg' {
   const value: string;
   export default value;
 }
  declare module '*.png' {
   const value: string;
   export default value;
 }
  declare module '*.gif' {
   const value: string;
   export default value;
 }
  declare module '*.svg' {
   const value: string;
   export default value;
 }
